package com.wind.config;import com.alibaba.druid.pool.DruidDataSource;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.autoconfigure.AutoConfigureAfter;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import javax.sql.DataSource;import java.sql.SQLException;@Configuration@AutoConfigureAfter(DruidAutoConfig.class)public class AuthorizationServerConfig {    @Autowired    private DruidProperties properties;    private DruidDataSource dataSource;    @Bean    public DataSource dataSource() {        dataSource = new DruidDataSource();        dataSource.setUrl(properties.getUrl());        dataSource.setUsername(properties.getUsername());        dataSource.setPassword(properties.getPassword());        dataSource.setInitialSize(properties.getInitialSize());        dataSource.setMinIdle(properties.getMinIdle());        dataSource.setMaxActive(properties.getMaxActive());        dataSource.setTestOnBorrow(properties.isTestOnBorrow());        try {            dataSource.setFilters(properties.getFilters());            dataSource.init();        } catch (SQLException e) {            throw new RuntimeException(e);        }        return dataSource;    }}