package com.wind.mybatis.pojo;import org.springframework.format.annotation.DateTimeFormat;import javax.persistence.Column;import javax.persistence.Id;import javax.persistence.Table;import java.util.Date;@Table(name = "order_info")public class OrderInfo {    @Id    private Integer id;    private Integer userid;    @Column(name = "ticketName")    private String ticketname;    @Column(name = "travelName")    private String travelname;    private Double price;    @Column(name = "travelDays")    private Double traveldays;    @Column(name = "buyDate")    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date buydate;    @Column(name = "goDate")    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date godate;    /**     * @return id     */    public Integer getId() {        return id;    }    /**     * @param id     */    public void setId(Integer id) {        this.id = id;    }    /**     * @return userid     */    public Integer getUserid() {        return userid;    }    /**     * @param userid     */    public void setUserid(Integer userid) {        this.userid = userid;    }    /**     * @return ticketName     */    public String getTicketname() {        return ticketname;    }    /**     * @param ticketname     */    public void setTicketname(String ticketname) {        this.ticketname = ticketname;    }    /**     * @return travelName     */    public String getTravelname() {        return travelname;    }    /**     * @param travelname     */    public void setTravelname(String travelname) {        this.travelname = travelname;    }    /**     * @return price     */    public Double getPrice() {        return price;    }    /**     * @param price     */    public void setPrice(Double price) {        this.price = price;    }    /**     * @return travelDays     */    public Double getTraveldays() {        return traveldays;    }    /**     * @param traveldays     */    public void setTraveldays(Double traveldays) {        this.traveldays = traveldays;    }    /**     * @return buyDate     */    public Date getBuydate() {        return buydate;    }    /**     * @param buydate     */    public void setBuydate(Date buydate) {        this.buydate = buydate;    }    /**     * @return goDate     */    public Date getGodate() {        return godate;    }    /**     * @param godate     */    public void setGodate(Date godate) {        this.godate = godate;    }}